// Import ZZ from '@aureooms/js-integer-ring' ;
import {_chr} from '@aureooms/js-integer-big-endian';
import {ZZ, DEFAULT_DISPLAY_BASE} from '@aureooms/js-integer';
import RationalField from './RationalField.js';

const domain = {
	name: 'ZZ',
	add: (a, b) => a.add(b),
	sub: (a, b) => a.sub(b),
	mul: (a, b) => a.mul(b),
	muln: (a, b) => a.muln(b),
	div: (a, b) => a.div(b),
	reg: (x, base) => ZZ.from(x, base),
	from: (x, base) => ZZ.from(x, base),
	str: (x, base) => x.toString(base),
	jz: (x) => x.iszero(),
	eq1: (x) => x.isone(),
	lt0: (x) => x.sign() < 0,
	gt1: (x) => x.gtn(1),
	cmp: (a, b) => a.cmp(b),
	eq: (a, b) => a.eq(b),
	sgn: (x) => x.sign(),
	abs: (x) => x.abs(),
	neg: (x) => x.opposite(),
	divmod: (a, b) => a.divmod(b),
	divmodn: (a, b) => a.divmodn(b),
	egcd: (a, b) => {
		const {u, v} = a.egcd(b);
		return {
			u: u.iabs(),
			v: v.iabs(),
		};
	},
	pown: (x, n) => x.pown(n),
	_chr,
	has: (...args) => ZZ.has(...args),
};

export default new RationalField(
	'Rational Field',
	domain,
	DEFAULT_DISPLAY_BASE,
);

import {__factorize__} from '@aureooms/js-prime';
import {$2, iadd1, eq0, gt1, divmod} from '@aureooms/js-number';
import {
	_add,
	_sub,
	_mul,
	_div,
	_pow,
	_cmp_no_bounds,
	_simplify,
	_digits,
	_stringify_digits,
	_parse_fraction,
	_parse_fixed_point,
} from '@aureooms/js-rational';

export default function _make_methods(domain) {
	const primefactors = new Map();
	const factorize = __factorize__($2, iadd1, eq0, gt1, divmod);
	const ufactors = (n) => {
		if (!primefactors.has(n)) primefactors.set(n, new Set(factorize(n)));
		return primefactors.get(n);
	};

	const add = _add(domain);
	const sub = _sub(domain);
	const mul = _mul(domain);
	const div = _div(domain);
	const pow = _pow(domain);
	const cmp = _cmp_no_bounds(domain);
	const simplify = _simplify(domain);

	const stringify_fraction = (x, d, b) =>
		domain.eq1(d)
			? domain.str(x, b)
			: `${domain.str(x, b)}/${domain.str(d, b)}`;

	const digits = _digits(domain);
	const stringify_digits = _stringify_digits(domain);
	const stringify_fixed_point = (x, d, b) =>
		stringify_digits(b, digits(b, ufactors(b), x, d));

	const parse_fraction = _parse_fraction(domain);
	const parse_fixed_point = _parse_fixed_point(domain);

	return {
		add,
		sub,
		mul,
		div,
		pow,
		cmp,
		simplify,
		stringify_fixed_point,
		stringify_fraction,
		parse_fraction,
		parse_fixed_point,
	};
}

import _make_methods from './_make_methods.js';

import {ZeroDivisionError} from '@aureooms/js-integer';

export default function _make_rational_class(domain, default_display_base) {
	const {
		add,
		sub,
		mul,
		div,
		pow,
		cmp,
		simplify,
		stringify_fixed_point,
		stringify_fraction,
		parse_fraction,
		parse_fixed_point,
	} = _make_methods(domain);

	class Rational {
		constructor(n, d) {
			this.n = n;
			this.d = d;
		}

		add(other) {
			const [a, b] = add(this.n, this.d, other.n, other.d);
			const [c, d] = simplify(a, b);
			return new Rational(c, d);
		}

		sub(other) {
			const [a, b] = sub(this.n, this.d, other.n, other.d);
			const [c, d] = simplify(a, b);
			return new Rational(c, d);
		}

		mul(other) {
			const [a, b] = mul(this.n, this.d, other.n, other.d);
			const [c, d] = simplify(a, b);
			return new Rational(c, d);
		}

		div(other) {
			if (other.isZero()) throw new ZeroDivisionError('Rational.div');
			const [a, b] = div(this.n, this.d, other.n, other.d);
			const [c, d] = simplify(a, b);
			return new Rational(c, d);
		}

		neg() {
			return new Rational(domain.neg(this.n), this.d);
		}

		isZero() {
			return domain.jz(this.n);
		}

		sign() {
			return domain.sgn(this.n);
		}

		inv() {
			if (domain.jz(this.n)) throw new ZeroDivisionError('Rational.inv');
			if (domain.sign(this.n) > 0) return new Rational(this.d, this.n);
			if (domain.sign(this.n) < 0)
				return new Rational(domain.neg(this.d), domain.neg(this.n));
		}

		pow(n) {
			if (!Number.isInteger(n))
				throw new Error('Rational.pow only accepts integer argument.');
			const [a, b] = pow(this.n, this.d, n);
			return new Rational(a, b);
		}

		cmp(other) {
			return cmp(this.n, this.d, other.n, other.d);
		}

		eq(other) {
			return this.cmp(other) === 0;
		}

		ne(other) {
			return this.cmp(other) !== 0;
		}

		gt(other) {
			return this.cmp(other) > 0;
		}

		lt(other) {
			return this.cmp(other) < 0;
		}

		ge(other) {
			return this.cmp(other) >= 0;
		}

		le(other) {
			return this.cmp(other) <= 0;
		}

		toString(base = default_display_base) {
			return stringify_fraction(this.n, this.d, base);
		}

		toNumber() {
			throw new Error('Rational.toNumber not implemented.');
		}

		toFixed(decimals = undefined, base = default_display_base) {
			const s = stringify_fixed_point(this.n, this.d, base);
			if (decimals === undefined) return s;

			const [left, right] = (s + '.').split('.');
			const [transient, repetend] = (right + '|0').split('|');

			const _right = (
				transient +
				new Array(Math.ceil(decimals / repetend.length) + 1).join(repetend)
			).slice(0, decimals);

			return left + '.' + _right.slice(0, decimals);
		}
	}

	Rational._parse_fraction = parse_fraction;
	Rational._parse_fixed_point = parse_fixed_point;
	Rational._simplify = simplify;

	return Rational;
}

import QQ from './QQ.js';
import RationalField from './RationalField.js';
import _make_methods from './_make_methods.js';
import _make_rational_class from './_make_rational_class.js';

export default QQ;

export {QQ, RationalField, _make_methods, _make_rational_class};

